<template>
  <q-dialog
    :model-value="props.modelValue"
    @update:model-value="updateValue"
    persistent
    transition-show="jump-up"
    transition-hide="jump-down">
    <q-card :dark="false" class="q-py-lg text-black login text-center">
      <div class="title">微信登录</div>
      <div class="sub-title">使用微信扫码登录</div>
      <q-img class="code"
             :src="code?.base64Code"
             placeholder-src="data:image/png;base64,"
      >
        <div v-if="scan" class="absolute-full flex flex-center">
          <div class="text-subtitle1">
            请在小程序中确认登录<br />
            如有其他问题，请尝试<span class="text-blue-4 cursor-pointer" @click="handleAuthCode">刷新二维码</span>
          </div>
        </div>
      </q-img>
      <div class="tip">登录即代表您同意用户协议</div>
      <q-btn @click="updateValue(false)" class="close" outline round size="10px" color="grey-6" icon="close"/>
    </q-card>
  </q-dialog>
</template>

<script lang="ts" setup>

import { ref, watch } from 'vue'
import { authCodeMini, authMini } from 'src/api/Auth'
import { AuthCode } from 'src/model/Auth'
import { useApplicationStore } from '@/stores/useApplicationStore'

interface LoginProps {
  modelValue: boolean
}

const props = defineProps<LoginProps>()
const emit = defineEmits(['update:modelValue'])

const code = ref<AuthCode | null>(null)
const scan = ref<boolean>(false)
const interval = ref<ReturnType<typeof setInterval> | null>(null)
const updateValue = (value: boolean) => emit('update:modelValue', value)
const applicationStore = useApplicationStore()

const handleAuthCode = async () => {
  if (interval.value !== null) {
    clearInterval(interval.value)
  }
  code.value = await authCodeMini()
  interval.value = setInterval(async () => {
    if (code.value === null) {
      return
    }
    const token = await authMini(code.value.authCode)
    console.log(token.id, token)
    if (token.id === 'true') {
      scan.value = true
    }
    if (token.accessToken !== null && interval.value !== null) {
      clearInterval(interval.value)
      applicationStore.setToken(token)
    }
  }, 3000)
}

watch(() => props.modelValue, async (val: boolean) => {
  if (interval.value !== null) {
    clearInterval(interval.value)
  }
  scan.value = false
  if (!val) {
    code.value = null
    return
  }
  await handleAuthCode()
})

</script>

